import React, { useState } from 'react';
import { GoogleMap, useJsApiLoader, Marker, InfoWindow } from '@react-google-maps/api';

const mapContainerStyle = {
  width: '100%',
  height: '600px',
  borderRadius: '8px',
};

// T·ªça ƒë·ªô trung t√¢m m·∫∑c ƒë·ªãnh (v√≠ d·ª•: trung t√¢m TP.HCM)
const defaultCenter = {
  lat: 10.7769,
  lng: 106.7009
};

function StoresMap({ stores }) {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY
  });

  // State ƒë·ªÉ l∆∞u tr·ªØ c·ª≠a h√†ng ƒëang ƒë∆∞·ª£c ch·ªçn (ƒë·ªÉ hi·ªán InfoWindow)
  const [selectedStore, setSelectedStore] = useState(null);

  if (!isLoaded) return <div>Loading Map...</div>;

  return (
    <GoogleMap
      mapContainerStyle={mapContainerStyle}
      center={defaultCenter}
      zoom={12}
    >
      {/* L·∫∑p qua danh s√°ch c·ª≠a h√†ng ƒë·ªÉ hi·ªÉn th·ªã c√°c Marker */}
      {stores.map((store) => (
        <Marker
          key={store.id}
          position={{
            lat: parseFloat(store.latitude),
            lng: parseFloat(store.longitude)
          }}
          icon={{
            path: "M12 2C8 2 5 5 5 9c0 5.25 7 13 7 13s7-7.75 7-13c0-4-3-7-7-7z", // SVG pin
            fillColor: "red",
            fillOpacity: 1,
            strokeWeight: 1,
            scale: 1.5,   // üëà ch·ªânh size ·ªü ƒë√¢y (tƒÉng/gi·∫£m)
            anchor: { x: 12, y: 24 },
          }}
          onClick={() => setSelectedStore(store)}
        />
      ))}

      {/* N·∫øu c√≥ m·ªôt c·ª≠a h√†ng ƒë∆∞·ª£c ch·ªçn, hi·ªÉn th·ªã InfoWindow */}
      {selectedStore && (
        <InfoWindow
          position={{
            lat: parseFloat(selectedStore.latitude),
            lng: parseFloat(selectedStore.longitude)
          }}
          onCloseClick={() => {
            setSelectedStore(null);
          }}
        >
          <div>
            <h4>{selectedStore.display_name || selectedStore.name}</h4>
            <p>{selectedStore.address}</p>
          </div>
        </InfoWindow>
      )}
    </GoogleMap>
  );
}

export default React.memo(StoresMap);
