import React, { useState, useEffect } from 'react';
import api from '../services/api';
import StoreForm from '../components/StoreForm';

function StoreListPage() {
    const [stores, setStores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // State cho Modal Form (chúng ta sẽ chỉ dùng pattern này)
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentStore, setCurrentStore] = useState(null);

    useEffect(() => {
        fetchStores();
    }, []);

    const fetchStores = async () => {
        try {
            // Đã bỏ hardcode "Pharmacity" để lấy tất cả cửa hàng. 
            // Bạn có thể thêm lại bộ lọc nếu cần.
            const response = await api.get('/stores'); 
            setStores(response.data.data);
        } catch (err) {
            console.error(err);
            setError('Failed to fetch stores.');
        } finally {
            setLoading(false);
        }
    };

    // --- CÁC HÀM XỬ LÝ CHO MODAL ---

    const handleOpenEditModal = (store) => {
        setCurrentStore(store);
        setIsModalOpen(true);
    };

    // --- FIX: Thêm hàm onSave hoàn chỉnh ---
    const handleSaveStore = async (storeData) => {
        try {
            // API `PUT` cần có ID, còn `POST` thì không.
            // Backend của chúng ta trả về dữ liệu cửa hàng đã được cập nhật.
            if (storeData.id) {
                // Chế độ Sửa
                const response = await api.put(`/stores/${storeData.id}`, storeData);
                setStores(stores.map(s => s.id === storeData.id ? response.data.data : s));
            } else {
                // Chế độ Tạo mới (nếu bạn có nút Add New)
                // const response = await api.post('/stores', storeData);
                // setStores([response.data.data, ...stores]);
            }
            setIsModalOpen(false); // Đóng modal sau khi lưu thành công
        } catch (err) {
            console.error(err);
            setError(storeData.id ? 'Failed to update store.' : 'Failed to create store.');
        }
    };

    // --- CÁC HÀM XỬ LÝ CHO CÁC NÚT KHÁC ---

    const handleViewDetail = (store) => {
        alert(`Thông tin chi tiết:\n- Chain: ${store.chain_name}\n- Store Code: ${store.store_code || store.board_name}\n- Address: ${store.address_vi}`);
    };

    const handleDeleteStore = async (storeId) => {
        if (window.confirm('Are you sure you want to delete this store?')) {
            try {
                await api.delete(`/stores/${storeId}`);
                setStores(stores.filter(store => store.id !== storeId));
            } catch (err) {
                setError('Failed to delete store.');
            }
        }
    };

    if (loading) return <div>Loading stores...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <div>
            <h2>Store Management</h2>
            <div className="table-wrapper">
                <table>
                    <thead>
                        <tr>
                            <th>CHUỖI CỬA HÀNG</th>
                            <th>MÃ CỬA HÀNG</th>
                            <th>ĐỊA CHỈ</th>
                            <th>QUẬN/HUYỆN</th>
                            <th>STATUS</th>
                            <th>ACTIONS</th>
                        </tr>
                    </thead>
                    <tbody>
                        {stores.map((store) => (
                            <tr key={store.id}>
                                <td>{store.chain_name}</td>
                                <td>{store.board_name || store.store_code}</td>
                                <td>{store.address_vi}</td>
                                <td>{store.district}</td>
                                <td style={{ color: store.is_active ? 'green' : 'red', fontWeight: 'bold' }}>
                                    {store.is_active ? 'Active' : 'Inactive'}
                                </td>
                                <td className="actions-cell">
                                    {/* --- SIMPLIFIED: Các nút hành động trực tiếp --- */}
                                    <button onClick={() => handleViewDetail(store)}>View</button>
                                    <button onClick={() => handleOpenEditModal(store)}>Edit</button>
                                    <button className="danger" onClick={() => handleDeleteStore(store.id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Modal Form để chỉnh sửa */}
            <StoreForm
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onSave={handleSaveStore}
                initialData={currentStore}
            />
        </div>
    );
}

export default StoreListPage;
