import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import api from '../services/api';

function CampaignDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [campaign, setCampaign] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCampaign = async () => {
      try {
        const res = await api.get(`/campaigns/${id}`);
        setCampaign(res.data);
      } catch (err) {
        console.error('Error fetching campaign', err);
        setError('Failed to load campaign details');
      } finally {
        setLoading(false);
      }
    };

    fetchCampaign();
  }, [id]);

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this campaign?')) return;
    try {
      await api.delete(`/campaigns/${id}`);
      navigate('/campaigns');
    } catch (err) {
      console.error('Error deleting campaign', err);
      alert('Failed to delete campaign');
    }
  };

  if (loading) return <p>Loading campaign...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!campaign) return <p>No campaign found</p>;

  return (
    <div className="detail-page">
      <h2>Campaign Detail</h2>
      <div className="detail-card">
        <p><strong>Name:</strong> {campaign.name}</p>
        <p><strong>Description:</strong> {campaign.description}</p>
        <p><strong>Start Date:</strong> {new Date(campaign.start_date).toLocaleDateString()}</p>
        <p><strong>End Date:</strong> {new Date(campaign.end_date).toLocaleDateString()}</p>
        <p>
          <strong>Status:</strong>{' '}
          <span style={{ color: campaign.status === 'active' ? 'green' : 'red' }}>
            {campaign.status}
          </span>
        </p>
      </div>

      <div className="detail-actions">
        <Link to={`/campaigns/${id}/edit`} className="btn-primary">
          Edit
        </Link>
        <button className="btn-danger" onClick={handleDelete}>
          Delete
        </button>
        <Link to="/campaigns" className="btn-secondary">
          Back to List
        </Link>
      </div>
    </div>
  );
}

export default CampaignDetailPage;
