// src/components/AssignStoreModal.jsx
import React, { useState, useEffect } from 'react';
import Modal from './Modal'; // Tận dụng component Modal đã có
import api from '../services/api';

function AssignStoreModal({ isOpen, onClose, onSave, campaignId, initiallyAssignedIds }) {
  const [allStores, setAllStores] = useState([]);
  const [selectedStoreIds, setSelectedStoreIds] = useState(new Set());
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Fetch tất cả store khi modal được mở
  useEffect(() => {
    if (isOpen) {
      const fetchAllStores = async () => {
        setLoading(true);
        try {
          const res = await api.get('/stores'); // API lấy tất cả store
          setAllStores(res.data.data || []);
          setSelectedStoreIds(new Set(initiallyAssignedIds));
        } catch (err) {
          setError('Failed to load stores.');
        } finally {
          setLoading(false);
        }
      };
      fetchAllStores();
    }
  }, [isOpen, initiallyAssignedIds]);

  // Xử lý khi check/uncheck một store
  const handleToggleStore = (storeId) => {
    setSelectedStoreIds(prev => {
      const newSet = new Set(prev);
      if (newSet.has(storeId)) {
        newSet.delete(storeId);
      } else {
        newSet.add(storeId);
      }
      return newSet;
    });
  };
  
  // Xử lý khi nhấn nút Save
  const handleSave = async () => {
    const originalIds = new Set(initiallyAssignedIds);
    const newIds = selectedStoreIds;

    const idsToAdd = [...newIds].filter(id => !originalIds.has(id));
    const idsToRemove = [...originalIds].filter(id => !newIds.has(id));

    setLoading(true);
    try {
      const addPromises = idsToAdd.map(store_id => 
        api.post(`/campaigns/${campaignId}/stores`, { store_id })
      );
      const removePromises = idsToRemove.map(storeId => 
        api.delete(`/campaigns/${campaignId}/stores/${storeId}`)
      );
      
      await Promise.all([...addPromises, ...removePromises]);
      onSave(); // Gọi hàm onSave từ props để đóng modal và refresh list
    } catch (err) {
      setError('Failed to save changes.');
    } finally {
      setLoading(false);
    }
  };


  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <div style={{ padding: '20px', minWidth: '500px' }}>
        <h3>Assign Stores</h3>
        {error && <p style={{ color: 'red' }}>{error}</p>}

        <div style={{ maxHeight: '400px', overflowY: 'auto', border: '1px solid #ccc', padding: '10px', margin: '15px 0' }}>
          {loading && !allStores.length ? <p>Loading stores...</p> : (
            allStores.map(store => (
              <div key={store.id}>
                <label>
                  <input
                    type="checkbox"
                    checked={selectedStoreIds.has(store.id)}
                    onChange={() => handleToggleStore(store.id)}
                  />
                  {store.board_name || store.store_code} - {store.address_vi}
                </label>
              </div>
            ))
          )}
        </div>

        <div style={{ marginTop: '20px' }}>
          <button onClick={handleSave} disabled={loading}>
            {loading ? 'Saving...' : 'Save Changes'}
          </button>
          <button onClick={onClose} style={{ marginLeft: '10px' }} className="danger">
            Cancel
          </button>
        </div>
      </div>
    </Modal>
  );
}

export default AssignStoreModal;
