import React, { useState, useEffect } from 'react';
import api from '../services/api';
import StoreForm from '../components/StoreForm'; // Import form modal

function StoreListPage() {
    const [stores, setStores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Modal state
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentStore, setCurrentStore] = useState(null);

    useEffect(() => {
        fetchStores();
    }, []);

    const fetchStores = async () => {
        try {
            // Gọi đúng API backend: /stores/chain/:chain
            const response = await api.get('/stores/chain/Pharmacity');
            setStores(response.data.data);
        } catch (err) {
            console.error(err);
            setError('Failed to fetch stores.');
        } finally {
            setLoading(false);
        }
    };

    const handleOpenCreateModal = () => {
        setCurrentStore(null);
        setIsModalOpen(true);
    };

    const handleOpenEditModal = (store) => {
        setCurrentStore(store);
        setIsModalOpen(true);
    };

    const handleSaveStore = async (storeData) => {
        try {
            if (storeData.store_id) {
                // Update
                const response = await api.put(`/stores/${storeData.store_id}`, storeData);
                setStores(
                    stores.map((s) =>
                        s.store_id === storeData.store_id ? response.data.data : s
                    )
                );
            } else {
                // Create
                const response = await api.post('/stores', storeData);
                setStores([response.data.data, ...stores]);
            }
            setIsModalOpen(false);
        } catch (err) {
            console.error(err);
            setError(storeData.store_id ? 'Failed to update store.' : 'Failed to create store.');
        }
    };

    const handleDeleteStore = async (storeId) => {
        if (window.confirm('Are you sure you want to delete this store?')) {
            try {
                await api.delete(`/stores/${storeId}`);
                setStores(stores.filter((store) => store.store_id !== storeId));
            } catch (err) {
                console.error(err);
                setError('Failed to delete store.');
            }
        }
    };

    if (loading) return <div>Loading stores...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <div>
            <h2>Store Management</h2>
            <button onClick={handleOpenCreateModal} style={{ marginBottom: '20px' }}>
                Add New Store
            </button>
            <button onClick={handleOpenCreateModal} style={{ marginBottom: '20px' }}>
                Pharmacity
            </button>
            <button onClick={handleOpenCreateModal} style={{ marginBottom: '20px' }}>
                Satrafoods
            </button>
            <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                    <tr>
                        <th>Chain</th>
                        <th>Board Name</th>
                        <th>Address (VI)</th>
                        <th>Latitude</th>
                        <th>Longitude</th>
                        <th>Status</th>
                        <th style={{ width: '150px' }}>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {stores.map((store) => (
                        <tr key={store.store_id}>
                            <td>{store.chain_name}</td>
                            <td>{store.board_name}</td>
                            <td>{store.address_vi}</td>
                            <td>{store.latitude}</td>
                            <td>{store.longitude}</td>
                            <td>{store.is_active ? 'Active' : 'Inactive'}</td>
                            <td class="actions-cell">
                                <button onClick={() => handleOpenEditModal(store)}>Edit</button>
                                <button
                                    onClick={() => handleDeleteStore(store.store_id)}
                                    style={{ marginLeft: '5px' }}
                                >
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Modal Form */}
            <StoreForm
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onSave={handleSaveStore}
                initialData={currentStore}
            />
        </div>
    );
}

export default StoreListPage;
