// src/components/AssignStoreModal.jsx
import React, { useState, useEffect } from "react";
import BaseModal from "../BaseModal";
import FilterBar from "../FilterBar";
import api from "../../services/api";
import { provinces } from "../../constants/locationData";

function AssignStoreModal({
  isOpen,
  onClose,
  onSave,
  campaignId,
  initiallyAssignedIds,
}) {
  const [allStores, setAllStores] = useState([]);
  const [selectedStoreIds, setSelectedStoreIds] = useState(new Set());
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Filter state
  const [filters, setFilters] = useState({
    province: "",
    district: "",
    search: "",
  });

  useEffect(() => {
    if (isOpen) {
      setFilters({ search: "", province: "all" });
      setLoading(true);
      setError("");
      api
        .get("/stores")
        .then((res) => {
          setAllStores(res.data.data || []);
          setSelectedStoreIds(new Set(initiallyAssignedIds));
        })
        .catch(() => setError("Không thể tải danh sách cửa hàng."))
        .finally(() => setLoading(false));
    }
  }, [isOpen, initiallyAssignedIds]);

  const handleToggleStore = (id) => {
    setSelectedStoreIds((prev) => {
      const copy = new Set(prev);
      copy.has(id) ? copy.delete(id) : copy.add(id);
      return copy;
    });
  };

  const handleSave = async () => {
    const orig = new Set(initiallyAssignedIds);
    const curr = selectedStoreIds;
    const add = [...curr].filter((id) => !orig.has(id));
    const remove = [...orig].filter((id) => !curr.has(id));

    setLoading(true);
    setError("");
    try {
      await Promise.all([
        ...add.map((id) =>
          api.post(`/campaigns/${campaignId}/stores`, { store_id: id })
        ),
        ...remove.map((id) =>
          api.delete(`/campaigns/${campaignId}/stores/${id}`)
        ),
      ]);
      onSave();
    } catch {
      setError("Lưu thay đổi thất bại.");
    } finally {
      setLoading(false);
    }
  };

  // District options based on selected province
  const districtOptions = filters.province ? districtMap[filters.province] || [] : [];

  // Filtered stores
  const filteredStores = useMemo(() => {
    return stores.filter((store) => {
      const matchProvince = filters.province ? store.district === filters.province : true;
      const matchDistrict = filters.district ? store.district_raw === filters.district : true;
      const matchSearch =
        (store.board_name || "").toLowerCase().includes(filters.search.toLowerCase()) ||
        (store.store_code || "").toLowerCase().includes(filters.search.toLowerCase()) ||
        (store.address || "").toLowerCase().includes(filters.search.toLowerCase());
      return matchProvince && matchDistrict && matchSearch;
    });
  }, [stores, filters]);

  return (
    <BaseModal
      isOpen={isOpen}
      onClose={onClose}
      title="Phân công cửa hàng cho chiến dịch"
      actions={
        <>
          <button
            className="btn btn-danger btn-sm"
            onClick={onClose}
            disabled={loading}
          >
            Hủy
          </button>
          <button
            className="btn btn-primary btn-sm"
            onClick={handleSave}
            disabled={loading}
          >
            {loading ? "Đang lưu..." : "Lưu thay đổi"}
          </button>
        </>
      }
    >
      {error && <p className="alert alert-error">{error}</p>}

      {/* FilterBar */}
      <FilterBar
        filters={filterConfig}
        values={filters}
        onChange={(name, value) =>
          setFilters((prev) => ({ ...prev, [name]: value }))
        }
        onReset={() => setFilters({ search: "", province: "all" })}
      />

      {/* Store list */}
      <div
        className="modal-content"
        style={{ maxHeight: "300px", overflowY: "auto" }}
      >
        {filteredStores.map((store) => (
          <label key={store.id} className="modal-row">
            <input
              type="checkbox"
              checked={selectedStoreIds.has(store.id)}
              onChange={() => handleToggleStore(store.id)}
            />
            <div className="modal-label">{store.store_code}</div>
            <div className="modal-value">{store.address}</div>
          </label>
        ))}
      </div>
    </BaseModal>
  );
}

export default AssignStoreModal;
