import React from 'react';
import { NavLink, Outlet } from 'react-router-dom'; // 1. Sử dụng NavLink thay cho Link
import { useAuth } from '../context/AuthContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTachometerAlt, faStore, faUsers, faBullhorn } from '@fortawesome/free-solid-svg-icons';

function Layout() {
    const { user, logout } = useAuth();

    // Style cho NavLink khi active
    const activeLinkStyle = {
      color: 'var(--primary-color)',
      fontWeight: 'bold',
    };

    return (
        <div>
            <nav className="navbar">
                <div className="nav-links">
                    {/* 2. Thêm icon và sử dụng NavLink */}
                    <NavLink to="/" className="nav-link" style={({ isActive }) => isActive ? activeLinkStyle : undefined}>
                        <FontAwesomeIcon icon={faTachometerAlt} /> Dashboard
                    </NavLink>
                    <NavLink to="/stores" className="nav-link" style={({ isActive }) => isActive ? activeLinkStyle : undefined}>
                        <FontAwesomeIcon icon={faStore} /> Stores
                    </NavLink>
                    <NavLink to="/users" className="nav-link" style={({ isActive }) => isActive ? activeLinkStyle : undefined}>
                        <FontAwesomeIcon icon={faUsers} /> Users
                    </NavLink>
                    <NavLink to="/campaigns" className="nav-link" style={({ isActive }) => isActive ? activeLinkStyle : undefined}>
                        <FontAwesomeIcon icon={faBullhorn} /> Campaigns
                    </NavLink>
                </div>

                {user && (
                    <div className="user-info">
                        <span>Welcome, {user.full_name}</span>
                        <span>|</span>
                        {/* 3. Cập nhật className cho nút Logout */}
                        <button className="btn btn-link" onClick={logout}>Logout</button>
                    </div>
                )}
            </nav>
            <main>
                {/* 4. Bọc Outlet trong .container */}
                <div className="container">
                    <Outlet />
                </div>
            </main>
        </div>
    );
}

export default Layout;

