import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';
import CampaignForm from '../components/CampaignForm';

function CampaignListPage() {
  const [campaigns, setCampaigns] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentCampaign, setCurrentCampaign] = useState(null);

  const fetchCampaigns = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get('/campaigns');
      setCampaigns(response.data.data);
    } catch (err) {
      setError('Failed to fetch campaigns.');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchCampaigns();
  }, [fetchCampaigns]);

  const handleOpenCreateModal = () => {
    setCurrentCampaign(null);
    setIsModalOpen(true);
  };

  const handleOpenEditModal = (campaign) => {
    setCurrentCampaign(campaign);
    setIsModalOpen(true);
  };

  const handleSaveCampaign = async (campaignData) => {
    try {
      if (campaignData.id) {
        const { id, ...updateData } = campaignData;
        const response = await api.put(`/campaigns/${id}`, updateData);
        setCampaigns(campaigns.map(c => c.id === id ? response.data.data : c));
      } else {
        const response = await api.post('/campaigns', campaignData);
        setCampaigns([response.data.data, ...campaigns]);
      }
      setIsModalOpen(false);
    } catch (err) {
      setError(campaignData.id ? 'Failed to update campaign.' : 'Failed to create campaign.');
    }
  };

  const handleDeleteCampaign = async (campaign) => {
    if (window.confirm(`Are you sure you want to delete campaign "${campaign.name}"?`)) {
      try {
        await api.delete(`/campaigns/${campaign.id}`);
        setCampaigns(campaigns.filter(c => c.id !== campaign.id));
      } catch (err) {
        setError('Failed to delete campaign.');
      }
    }
  };

  if (loading) return <div className="loading-message">Loading campaigns...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="container">
      <div className="page-header flex-between">
        <h2 className="page-title">Campaign Management</h2>
        <button onClick={handleOpenCreateModal} className="btn btn-primary">
          + Add New Campaign
        </button>
      </div>

      <div className="table-wrapper">
        <table className="data-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Start Date</th>
              <th>End Date</th>
              <th>Status</th>
              <th className="actions-header">Actions</th>
            </tr>
          </thead>
          <tbody>
            {campaigns.map(campaign => (
              <tr key={campaign.id}>
                <td>
                  <Link to={`/campaigns/${campaign.id}`} className="link">
                    {campaign.name}
                  </Link>
                </td>
                <td className="truncate-text" title={campaign.description}>
                  {campaign.description}
                </td>
                <td>{new Date(campaign.start_date).toLocaleDateString('vi-VN')}</td>
                <td>{new Date(campaign.end_date).toLocaleDateString('vi-VN')}</td>
                <td>
                  <span className={`status-badge ${campaign.is_active ? 'status-active' : 'status-inactive'}`}>
                    {campaign.is_active ? 'Active' : 'Inactive'}
                  </span>
                </td>
                <td className="actions-cell">
                  <button className="btn btn-outline" onClick={() => handleOpenEditModal(campaign)}>
                    Edit
                  </button>
                  <button className="btn btn-danger" onClick={() => handleDeleteCampaign(campaign)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <CampaignForm
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSave={handleSaveCampaign}
        initialData={currentCampaign}
      />
    </div>
  );
}

export default CampaignListPage;
