import React, { useState, useEffect } from 'react';
import api from '../services/api';
import StoreForm from '../components/StoreForm'; // Import the new form

function StoreListPage() {
    const [stores, setStores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // --- NEW: State to manage the modal ---
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentStore, setCurrentStore] = useState(null); // To hold data for editing

    useEffect(() => {
        fetchStores();
    }, []);

    const fetchStores = async () => {
        try {
            const response = await api.get('/stores');
            setStores(response.data.data);
        } catch (err) {
            setError('Failed to fetch stores.');
        } finally {
            setLoading(false);
        }
    };

    // --- NEW: Handlers to open the modal ---
    const handleOpenCreateModal = () => {
        setCurrentStore(null); // No initial data for create
        setIsModalOpen(true);
    };

    const handleOpenEditModal = (store) => {
        setCurrentStore(store); // Pass store data for editing
        setIsModalOpen(true);
    };

    // --- NEW: Handler to save data from the modal ---
    const handleSaveStore = async (storeData) => {
        try {
            if (storeData.id) {
                // Update existing store
                const response = await api.put(`/stores/${storeData.id}`, storeData);
                setStores(stores.map(s => s.id === storeData.id ? response.data.data : s));
            } else {
                // Create new store
                const response = await api.post('/stores', storeData);
                setStores([response.data.data, ...stores]);
            }
            setIsModalOpen(false); // Close modal on success
        } catch (err) {
            setError(storeData.id ? 'Failed to update store.' : 'Failed to create store.');
        }
    };

    const handleDeleteStore = async (storeId) => {
        if (window.confirm('Are you sure you want to delete this store?')) {
            try {
                await api.delete(`/stores/${storeId}`);
                setStores(stores.filter(store => store.id !== storeId));
            } catch (err) {
                setError('Failed to delete store.');
            }
        }
    };

    if (loading) return <div>Loading stores...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <div>
            <h2>Store Management</h2>
            <button onClick={handleOpenCreateModal} style={{ marginBottom: '20px' }}>
                Add New Store
            </button>
            
            <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Address</th>
                        <th>Latitude</th>
                        <th>Longitude</th>
                        <th>Status</th>
                        <th style={{width: '150px'}}>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {stores.map(store => (
                        <tr key={store.id}>
                            <td>{store.name}</td>
                            <td>{store.address}</td>
                            <td>{store.latitude}</td>
                            <td>{store.longitude}</td>
                            <td>{store.status}</td>
                            <td>
                                <button onClick={() => handleOpenEditModal(store)}>Edit</button>
                                <button onClick={() => handleDeleteStore(store.id)} style={{ marginLeft: '5px' }}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* --- The Modal Form --- */}
            <StoreForm 
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onSave={handleSaveStore}
                initialData={currentStore}
            />
        </div>
    );
}

export default StoreListPage;
