// src/components/AssignStoreModal.jsx
import React, { useState, useEffect } from "react";
import Modal from "../Modal";
import api from "../../services/api";

function AssignStoreModal({ isOpen, onClose, onSave, campaignId, initiallyAssignedIds }) {
  const [allStores, setAllStores] = useState([]);
  const [selectedStoreIds, setSelectedStoreIds] = useState(new Set());
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    if (isOpen) {
      const fetchStores = async () => {
        setLoading(true);
        setError("");
        setSearchTerm("");
        try {
          const res = await api.get("/stores");
          setAllStores(res.data.data || []);
          setSelectedStoreIds(new Set(initiallyAssignedIds));
        } catch {
          setError("Không thể tải danh sách cửa hàng.");
        } finally {
          setLoading(false);
        }
      };
      fetchStores();
    }
  }, [isOpen, initiallyAssignedIds]);

  const handleToggleStore = (storeId) => {
    setSelectedStoreIds((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(storeId)) newSet.delete(storeId);
      else newSet.add(storeId);
      return newSet;
    });
  };

  const handleSave = async () => {
    const originalIds = new Set(initiallyAssignedIds);
    const newIds = selectedStoreIds;
    const idsToAdd = [...newIds].filter((id) => !originalIds.has(id));
    const idsToRemove = [...originalIds].filter((id) => !newIds.has(id));

    setLoading(true);
    setError("");
    try {
      const addPromises = idsToAdd.map((store_id) =>
        api.post(`/campaigns/${campaignId}/stores`, { store_id })
      );
      const removePromises = idsToRemove.map((storeId) =>
        api.delete(`/campaigns/${campaignId}/stores/${storeId}`)
      );
      await Promise.all([...addPromises, ...removePromises]);
      onSave();
    } catch {
      setError("Lưu thay đổi thất bại.");
    } finally {
      setLoading(false);
    }
  };

  const filteredStores = allStores.filter((store) => {
    const text = `${store.board_name || ""} ${store.store_code || ""} ${store.address || ""}`.toLowerCase();
    return text.includes(searchTerm.toLowerCase());
  });

  return (
    <Modal isOpen={isOpen} onClose={onClose} className="modal">
      {/* Header */}
      <div className="modal-header">
        <h3>Phân công cửa hàng cho chiến dịch</h3>
      </div>

      {/* Content */}
      <div className="modal-content">
        {error && <p className="alert alert-error">{error}</p>}

        <div className="form-group">
          <input
            type="text"
            placeholder="Tìm kiếm theo chuỗi, mã, hoặc địa chỉ..."
            className="form-control"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        {/* Danh sách cửa hàng scrollable */}
        <div
          className="store-list table-wrapper"
          style={{ maxHeight: "300px", padding: "8px", overflowY: "auto" }}
        >
          {loading && !allStores.length ? (
            <p className="alert alert-info">Đang tải danh sách cửa hàng...</p>
          ) : filteredStores.length > 0 ? (
            filteredStores.map((store) => (
              <label
                key={store.id}
                className="card mb-2"
                style={{ display: "flex", alignItems: "center", padding: "8px", cursor: "pointer" }}
              >
                <input
                  type="checkbox"
                  checked={selectedStoreIds.has(store.id)}
                  onChange={() => handleToggleStore(store.id)}
                  style={{ marginRight: "12px" }}
                />
                <div className="detail-label" style={{ flex: 1 }}>
                  {store.store_code}
                </div>
                <div className="detail-value" style={{ flex: 2 }}>
                  {store.address}
                </div>
              </label>
            ))
          ) : (
            <p className="alert alert-warning">Không tìm thấy cửa hàng nào.</p>
          )}
        </div>
      </div>


      {/* Actions */}
      <div className="modal-actions">
        <button className="btn btn-danger btn-sm" onClick={onClose} disabled={loading}>
          Hủy
        </button>
        <button className="btn btn-primary btn-sm" onClick={handleSave} disabled={loading}>
          {loading ? "Đang lưu..." : "Lưu thay đổi"}
        </button>
      </div>
    </Modal>
  );
}

export default AssignStoreModal;
