import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../services/api';
import StoresMap from '../components/StoresMap';
import { districts, districtMap } from '../constants/locationData';

function DashBoardPage() {
  const { campaign_id } = useParams(); // 👈 lấy từ URL: /dashboard/:campaign_id
  const [stores, setStores] = useState([]);
  const [campaignStores, setCampaignStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError('');
      try {
        // gọi API song song
        const [storeRes, campaignStoreRes] = await Promise.all([
          api.get('/stores'),
          api.get(`/campaigns/${campaign_id}/stores`)
        ]);

        const allStores = storeRes.data.data || [];
        const cs = campaignStoreRes.data.data || [];

        setCampaignStores(cs);

        if (cs.length > 0) {
          const storeIds = new Set(cs.map(item => item.store_id));
          setStores(allStores.filter(store => storeIds.has(store.id)));
        } else {
          setStores(allStores);
        }
      } catch (err) {
        console.error(err);
        setError('Failed to load data.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [campaign_id]);

  return (
    <div className="container">
      <div className="page-header">
        <h2>Dashboard Overview</h2>
      </div>
      <div className="card">
        <div className="card-header">
          <h4>Store Locations</h4>
        </div>
        <div className="card-body">
          {loading && <p>Loading map data...</p>}
          {error && <p className="error-message">{error}</p>}
          {!loading && !error && (
            <StoresMap
              stores={stores}
              districts={districts}
              districtMap={districtMap}
              campaignStores={campaignStores}
            />
          )}
        </div>
      </div>
    </div>
  );
}

export default DashBoardPage;
