import React, { useState, useRef, useCallback, useEffect } from "react";
import { GoogleMap, useJsApiLoader, InfoWindow } from "@react-google-maps/api";

const mapContainerStyle = {
  width: "100%",
  height: "600px",
  borderRadius: "8px",
};

const defaultCenter = {
  lat: 10.7769,
  lng: 106.7009,
};

function StoresMap({ stores }) {
  const { isLoaded } = useJsApiLoader({
    id: "google-map-script",
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  });

  const mapRef = useRef(null);
  const [selectedStore, setSelectedStore] = useState(null);
  const markersRef = useRef([]);

  const onLoad = useCallback((map) => {
    mapRef.current = map;
  }, []);

  // Tạo AdvancedMarker khi stores thay đổi
  useEffect(() => {
    if (!mapRef.current || !isLoaded) return;

    // Xoá markers cũ
    markersRef.current.forEach((m) => m.setMap(null));
    markersRef.current = [];

    stores.forEach((store) => {
      const position = {
        lat: parseFloat(store.latitude),
        lng: parseFloat(store.longitude),
      };

      // Tạo nội dung marker tùy biến
      const markerDiv = document.createElement("div");
      markerDiv.style.width = "20px";
      markerDiv.style.height = "20px";
      markerDiv.style.borderRadius = "50%";
      markerDiv.style.background = "#ff4757";
      markerDiv.style.boxShadow = "0 0 10px rgba(255,71,87,0.6)";
      markerDiv.style.cursor = "pointer";

      const marker = new window.google.maps.marker.AdvancedMarkerElement({
        position,
        map: mapRef.current,
        content: markerDiv,
        title: store.display_name || store.name,
      });

      // Click event
      markerDiv.addEventListener("click", () => {
        setSelectedStore(store);
      });

      markersRef.current.push(marker);
    });
  }, [stores, isLoaded]);

  if (!isLoaded) return <div>Loading Map...</div>;

  return (
    <GoogleMap
      mapContainerStyle={mapContainerStyle}
      center={defaultCenter}
      zoom={12}
      onLoad={onLoad}
      options={{
        mapId: process.env.REACT_APP_GOOGLE_MAP_ID, // Bắt buộc khi dùng AdvancedMarker
        disableDefaultUI: false,
        clickableIcons: false,
      }}
    >
      {/* InfoWindow cho cửa hàng được chọn */}
      {selectedStore && (
        <InfoWindow
          position={{
            lat: parseFloat(selectedStore.latitude),
            lng: parseFloat(selectedStore.longitude),
          }}
          onCloseClick={() => setSelectedStore(null)}
        >
          <div>
            <h4>{selectedStore.display_name || selectedStore.name}</h4>
            <p>{selectedStore.address}</p>
          </div>
        </InfoWindow>
      )}
    </GoogleMap>
  );
}

export default React.memo(StoresMap);
